mapper.insert:
INSERT INTO property ( name, full_name, type, created_at
, parent_id
) VALUES ( #{name}, #{fullName}, #{type}, NOW()
, (SELECT a.id FROM property a WHERE a.full_name = #{parentFullName})
)

mapper.select:
SELECT name, full_name, type, value, created_at, updated_at
FROM property


        AND id = #{id}


        AND full_name = #{fullName}

mapper.update:
UPDATE property
SET
   full_name = #{newPropertyFullName}
